from itertools import product


class Solution:
    def numberOfWays(self, s: str) -> int:
        ones = [idx for idx, ch in enumerate(s) if ch == "1"]
        zeros = [idx for idx, ch in enumerate(s) if ch == "0"]
        zoz = len([x for x in product(zeros, ones, zeros) if x[0] < x[1] < x[2]])
        ozo = len([x for x in product(ones, zeros, ones) if x[0] < x[1] < x[2]])
        return zoz + ozo


x = Solution()
print(x.numberOfWays("11100"))
print(x.numberOfWays("001101"))
print(
    x.numberOfWays(
        ""
    )
)
